import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { DataSet, Network } from 'vis-network/standalone/esm/vis-network';

function GrafoVisualizacion() {
  const [datos, setDatos] = useState({ nodes: [], edges: [] });

  useEffect(() => {
    // Solicitar solo sismos con magnitud >= 5
    axios.get('http://localhost:8080/api/sismos/magnitud/5')
      .then(response => {
        const sismos = response.data;
        const nodes = sismos.map(sismo => ({
          id: sismo.id,
          label: `Magnitud: ${sismo.magnitud}\n${sismo.fecha}`,
          title: `Fecha: ${sismo.fecha}\nHora: ${sismo.hora}\nLatitud: ${sismo.latitud}\nLongitud: ${sismo.longitud}`,
          group: 'sismo'
        }));

        const edges = []; // Agrega lógica para crear las relaciones si es necesario

        setDatos({ nodes, edges });
      })
      .catch(error => {
        console.error('Error fetching data: ', error);
      });
  }, []);

  useEffect(() => {
    const container = document.getElementById('mynetwork');
    const data = {
      nodes: new DataSet(datos.nodes),
      edges: new DataSet(datos.edges)
    };
    const options = {};
    new Network(container, data, options);
  }, [datos]);

  return (
    <div>
      <h2>Visualización de Grafos</h2>
      <div id="mynetwork" style={{ height: '600px', border: '1px solid #ddd' }}></div>
    </div>
  );
}

export default GrafoVisualizacion;
